mapper.select:
    select activity.act_id,activity.rule_id,activity.`name`,activity.act_status,rule.conditions,activity.join_times,activity.member_lv_ids,activity.use_platform,activity.start_time,activity.end_time
	from t_activity_full_reduce activity,t_activity_rules rule
	where rule.rule_id=activity.rule_id and activity.act_id=#{actId,jdbcType=BIGINT}

    select activity.act_id,activity.rule_id,activity.`name`,activity.act_status,rule.conditions,activity.member_lv_ids,activity.start_time,activity.end_time
	from t_activity_full_reduce activity,t_activity_rules rule
	where rule.rule_id=activity.rule_id and activity.activity_type='1'
	 and COMPANY_ID = #{companyId,jdbcType=INTEGER}
	 and STORE_ID = #{storeId,jdbcType=INTEGER}
