mapper.select:
select t.cpns_id, t.cpns_name, t.issue_type, t.company_id, t.store_id, t.pmt_id, t.cpns_prefix, t.cpns_gen_quantity, t.cpns_key, t.cpns_status, t.cpns_point, t.rule_id, t.cpns_type, t.online_gen_quantity, t.online_limit, t.online_quantity, t.create_time, t.disabled,tc.store_name,t.big_pic,t.type,t.cnns_price,t.cnns_par ,DATE_FORMAT(srg.from_time, '%Y-%m-%d %H:%i:%s')from_time,DATE_FORMAT(srg.to_time, '%Y-%m-%d %H:%i:%s')to_time from t_coupons t left join t_company tc on t.company_id=tc.company_id left join t_sales_rule_goods srg on srg.rule_id=t.rule_id where t.disabled ='0'
and t.company_id = #{companyId,jdbcType=BIGINT}
and t.store_id = #{storeId,jdbcType=BIGINT}
and t.type = #{type,jdbcType=INTEGER}
