mapper.insert:

insert into t_car_data_index (data_id, data_type, order_id, 
  dataformat, display_name, list_id, 
  catalog_id, disabled, createtime
  )
values (#{dataId,jdbcType=INTEGER}, #{dataType,jdbcType=VARCHAR}, #{orderId,jdbcType=INTEGER}, 
  #{dataformat,jdbcType=VARCHAR}, #{displayName,jdbcType=VARCHAR}, #{listId,jdbcType=INTEGER}, 
  #{catalogId,jdbcType=INTEGER}, #{disabled,jdbcType=VARCHAR}, #{createtime,jdbcType=TIMESTAMP}
  )

mapper.select:
select data_id, data_type, tcd.order_id,tcd.createtime, dataformat, display_name, tcd.list_id, tcd.catalog_id, tcd.disabled, tcdc.catalog_name,tcdl.data, tcdl.list_name from t_car_data_index tcd join t_car_data_catalog tcdc on tcd.catalog_id = tcdc.catalog_id left join t_car_data_list tcdl on tcd.list_id = tcdl.list_id where tcd.disabled='0'
and display_name LIKE CONCAT('%', #{displayName,jdbcType=VARCHAR},'%')
and data_type LIKE CONCAT('%', #{dataType,jdbcType=VARCHAR},'%')
and tcd.catalog_id = #{catalogId,jdbcType=INTEGER}
ORDER BY ${orderStmt}

select count(data_id) from t_car_data_index 
where disabled='0' and  display_name = #{displayName,jdbcType=VARCHAR}

     and data_id != #{dataId,jdbcType=INTEGER}
