SELECT A.GOODS_ID, A.CAT_ID, D.CAT_NAME, D.CAT_LOGO, A.NAME, F.STORE_NAME, A.WEIGHT, A.SCORE,A.COMPANY_ID, A.LAST_MODIFY, A.UP_TIME, A.DOWN_TIME, A.STORE, A.MKT_PRICE, A.FBN, A.DBN, A.BRIEF, A.COST, B.BRAND_NAME, B.BRAND_ID, B.BRAND_LOGO, A.PRICE, A.FAV_COUNT, A.RANK_COUNT, A.COMMENTS_COUNT, A.VIEW_COUNT, A.VIEW_W_COUNT, A.VIEW_M_COUNT, A.BUY_COUNT, A.BUY_W_COUNT, A.BUY_M_COUNT, A.UNIT, A.MARKETABLE, A.THUMBNAIL_PIC, A.SMALL_PIC, A.MID_PIC, A.BIG_PIC,A.P_22
FROM T_GOODS A JOIN T_GOODS_CAT D ON D.CAT_ID = A.CAT_ID JOIN T_COMPANY F ON F.COMPANY_ID = A.COMPANY_ID LEFT JOIN T_BRAND B ON B.BRAND_ID = A.BRAND_ID
WHERE 1 = 1 and A.marketable != -1
and ${item}
ORDER BY ${orderStmt}

