where t.DISABLED="0" and MARKETABLE_ALLOW='0' 
 and MARKETABLE = #{marketableQ,jdbcType=VARCHAR}
 and NAME LIKE CONCAT('%', #{name,jdbcType=VARCHAR},'%')
 and FBN LIKE CONCAT('%', #{fbn,jdbcType=VARCHAR},'%')
 and 
	t.CAT_ID in 

		#{item}



 and t.COMPANY_ID = #{companyId,jdbcType=INTEGER}
 and STORE_ID = #{storeId,jdbcType=INTEGER} 


	  and PRICE >= #{minPrice,jdbcType=DECIMAL}




	  and PRICE <= #{maxPrice,jdbcType=DECIMAL}




	  and BUY_COUNT >= #{minBuyCount,jdbcType=DECIMAL}




	  and BUY_COUNT <= #{maxBuyCount,jdbcType=DECIMAL}


ORDER BY LAST_MODIFY desc

