mapper.select:
    select v.*,vc.cat_name
    from t_business_violation v 
    left join t_business_violationcat vc on v.cat_id = vc.cat_id
    where v.disabled = '0' 

    	and v.cat_id = #{catId}


    	and score_value = #{scoreValue}


    	and goods_days = #{goodsDays}


    	and goodsdown_days = #{goodsdownDays}


    	and news_days = #{newsDays}


    	and news_days_value = #{newsDaysValue}


    	and store_days = #{storeDays}


    	and storedown_days = #{storedownDays}


    	and sales_days = #{salesDays}



	    	and earnest_money > #{earnestMoney}


	    	and earnest_money >= #{earnestMoney}


	    	and earnest_money = #{earnestMoney}


	    	and #{earnestMoney} >= earnest_money


	    	and #{earnestMoney} > earnest_money 





		    	and earnest_money between #{earnestMoney1} and #{earnestMoney2}



    order by v.violation_id desc
